    public class WellPressureCalculationRequestDTO
    {
        /// <summary>
        /// Tubular information of the relevant casing components.
        /// </summary>        
        [Required]
        public IEnumerable<Tubular> tubulares { get; set; }

        /// <summary>
        /// Information about annulus input data. Contains density and pressure of annulus.
        /// </summary>
        [Required]
        public IEnumerable<Annulus> anulares { get; set; }
        
        
        /// <summary>
        /// Wellhead information. Includes the information the weakest Wellhead connected to each annulus
        /// </summary>
        [Required]
        public IEnumerable<Wellhead> cabezales { get; set; }
        
        /// <summary>
        /// Contains the lithological column information
        /// </summary>
        [Required]
        public IEnumerable<Formation> formaciones { get; set; }

        /// <summary>
        /// Contains BHA accessories that are relevant. To calculations and that may fail (Valves, packers, etc)
        /// </summary>
        [Required]
        public IEnumerable<Accessory> accesorios { get; set; }
        
        /// <summary>
        /// Contains the Liner hanger elements information
        /// </summary>
        [Required]
        public IEnumerable<LinerHanger> Liner_Hanger { get; set; }

        /// <summary>
        /// Contains the SecurityFactors information. Used in calculations.
        /// </summary>
        [Required]
        public Dictionary<string, double> SecurityFactors { get; set; }
        
        /// <summary>
        /// Survey stations array, used for interpolation of MDs into TVDs
        /// </summary>
        [Required]
        public IEnumerable<SurveyStation> Survey { get; set; }

        /// <summary>
        /// Information of reference depths.
        /// </summary>
        [Required]
        public DatumData ReferenceDepths { get; set; }
        
        /// <summary>
        /// Well perforations information
        /// </summary>
        [Required]
        public IEnumerable<Perforation> Perforations { get; set; }
        
        /// <summary>
        /// Pore pressure gradient lines array
        /// </summary>
        [Required]
        public IEnumerable<WellboreGradient> PorePressureGradient { get; set; }

        /// <summary>
        /// Fracture Pressure Gradient array
        /// </summary>
        public IEnumerable<WellboreGradient> FracturePressureGradient { get; set; }
    }
